import { Component, ElementRef, ViewChild, Renderer2 } from '@angular/core';
import { NavController, NavParams, IonicPage } from 'ionic-angular';
import { Camera, CameraOptions } from '@ionic-native/camera';

/**
 * Generated class for the QrReaderPage page.
 *
 * See https://ionicframework.com/docs/components/#navigation for more info on
 * Ionic pages and navigation.
 */

@IonicPage()
@Component({
  selector: 'page-qr-reader',
  templateUrl: 'qr-reader.html',
})
export class QrReaderPage {
  base64Image: string;
  data: string;
  elementType = 'url';
  //value = '../../assets/imgs/barcode.jpg';
  // value=`data:image/jpeg;base64,
  // 
  // 
  // `
  value = 'data:image/png;base64,'
  @ViewChild('result') resultElement: ElementRef;
  showQRCode: boolean = true;
  constructor(private camera: Camera, private renderer: Renderer2, public navCtrl: NavController) {
    console.log(this.base64Image)
  }

  ionViewDidLoad() {
    console.log('ionViewDidLoad QrReaderPage');
  }
  render(e) {
    var that = this;
    console.log(e);
    console.log(e.result);
    this.data = e.result
    console.log(this.data)
    let element: Element = this.renderer.createElement('p');
    element.innerHTML = e.result;
    this.renderElement(element);

  }

  renderElement(element) {
    for (let node of this.resultElement.nativeElement.childNodes) {
      this.renderer.removeChild(this.resultElement.nativeElement, node);
    }
    this.renderer.appendChild(this.resultElement.nativeElement, element);
  }

  getImage() {
    const options: CameraOptions = {
      quality: 70,
      destinationType: this.camera.DestinationType.DATA_URL,
      sourceType: this.camera.PictureSourceType.PHOTOLIBRARY,
      saveToPhotoAlbum: false,
      allowEdit: false

    }

    this.camera.getPicture(options).then((imageData) => {
      // imageData is either a base64 encoded string or a file URI
      // If it's base64 (DATA_URL):

      this.base64Image = 'data:image/png;base64,' + imageData;
      this.value = this.base64Image;
    }, (err) => {
      alert(err)
    });
  }





}
